// PDF.js Básico - Fallback para cuando no se puede cargar PDF.js completo
console.log('PDF.js fallback cargado');

// Crear objeto pdfjsLib básico si no existe
if (typeof window.pdfjsLib === 'undefined') {
    window.pdfjsLib = {
        getDocument: function(url) {
            console.error('PDF.js no está disponible. Archivo:', url);
            return {
                promise: Promise.reject(new Error('PDF.js no está disponible'))
            };
        },
        GlobalWorkerOptions: {
            workerSrc: null
        }
    };
    
    console.warn('⚠️ PDF.js no disponible. Usando fallback básico.');
    console.warn('Para mejor funcionalidad, carga PDF.js desde CDN:');
    console.warn('https://cdnjs.cloudflare.com/ajax/libs/pdf.js/3.11.174/pdf.min.js');
} 